import React, { Component } from 'react'
import ZoomImg from './ZoomImg';
import moment from "moment";

export default class LookSingle extends Component {
  constructor() {
    super()
    this.state={
      timeStamp: null
    }

  }

  componentDidMount() {
    this.setState({
      ...this.state,
      timeStamp: moment(this.props.created_at).format("dddd, MMMM Do YYYY, h:mm:ss a")
    })
    // this.timeStamp = moment(this.props.created_at).format("dddd, MMMM Do YYYY, h:mm:ss a")
    // console.log(this.props.created_at)
    // console.log(moment(this.props.created_at).isValid())

  }

  render() {
    console.log(this.timeStamp)
    console.log(this.props.created_at)
    return (
      <li className="singleLook">
        <div className="img-description-time-wrapper">
          <ZoomImg src={this.props.picture} />
          <p>Description: {this.props.lookDescription}</p>
          <span className="timestamp">{this.timeStamp}</span>
        </div>
      </li>
    )
  }
}
